import React, {Component} from 'react';
import axios from 'axios';
import swal from 'sweetalert2';
import { Button, Col, ControlLabel,FormGroup, FormControl, Table, Modal, Well } from 'react-bootstrap';


class Admin extends Component{
	constructor (props) {
		super(props);
		this.state = {
			users: [],
			currentUser: [],
		};
	}

	follow(user) {
		//console.log('this.state', this.state);
			fetch("http://127.0.0.1:8000/user/follow/"+user, {
				method: "PUT",
				headers: {
					'Accept': 'application/json',
					'Content-Type': 'application/json',
					'Authorization': localStorage.getItem('token')
				}
			})
			.then(function(res){
				return res;
			})
			.then(function(data){
				console.log(data.text);
				console.log("user successfully followed");
				swal('Success!', 'Followed!', 'success');
			});
	}

	unfollow(user) {
		//console.log('this.state', this.state);
			fetch("http://127.0.0.1:8000/user/unfollow/"+user, {
				method: "PUT",
				headers: {
					'Accept': 'application/json',
					'Content-Type': 'application/json',
					'Authorization': localStorage.getItem('token')
				}
			})
			.then(function(res){
				return res;
			})
			.then(function(data){
				console.log(data.text);
				console.log("user successfully unfollowed");
				swal('Success!', 'Unfollowed!', 'success');
			});
	}

	delete() {
			fetch("http://127.0.0.1:8000/user/delete", {
				method: "PUT",
				headers: {
					'Accept': 'application/json',
					'Content-Type': 'application/json',
					'Authorization': localStorage.getItem('token')
				}
			})
			.then(function(res){
				return res;
			})
			.then(function(data){
				console.log(data.text);
				console.log("user successfully deleted");
				swal('Success!', 'Deleted!', 'success');
				window.location = "/signin";
			});
	}

	getUsers() {
		axios.get('http://127.0.0.1:8000/user/users', { 'headers': { 'Authorization': localStorage.getItem("token")}})
		.then(res => {
			//console.log(res);
			//return res.data;
			this.setState({users:res.data});
		});
		return "done";
	}

	getCurrent() {
		axios.get('http://127.0.0.1:8000/user/me', { 'headers': { 'Authorization': localStorage.getItem("token")}})
		.then(res => {
			//console.log(res);
			//return res.data;
			this.setState({currentUser:res.data});
		});
		return "done";
	}

	edit() {
		console.log("edited");
	}

	componentDidMount() {
		this.getUsers();
		this.getCurrent();
	}



	render(){
		var users = this.state.users;
		var current = this.state.currentUser;
		console.log("state", this.state);
		return (
			<div className = "container">
			<h2>My profile</h2>
			{
				current.map((user) => {
					return(
						<div>
							<Well>
								<b>{user.username} </b><br/>
								{user.email}<br/>
								Tweets: <br/>
								Followers:
								<button
								className='btn pull-right btn-danger'
								type='button'
								onClick={() => this.delete()}>
								Delete
								</button>
								<button
								className='btn btn-primary pull-right'
								type='button'
								onClick={() => this.edit(user.username)}>
								Edit
								</button>
							</Well>
						</div>
					)
				})
			}

			<h2>All Users</h2>
			{
				users.map((user) => {
					return(
						<div>
							<Well>
								<b>{user.username} </b><br/>
								{user.email}<br/>
								Tweets: <br/>
								Followers:
								<button
								className='btn pull-right'
								type='button'
								onClick={() => this.unfollow(user.username)}>
								Unfollow
								</button>
								<button
								className='btn btn-primary pull-right'
								type='button'
								onClick={() => this.follow(user.username)}>
								Follow
								</button>
							</Well>
						</div>
					)
				})
			}

			</div>
			)
	}

}

export default Admin;
