import React, {Component} from 'react';
import swal from 'sweetalert2';

class Article extends Component{
	constructor (props) {
		super(props);
		this.state = {
			title:'',
			body: '',
			author_id: '',
		}
	}

	createArticle() {
		//console.log('this.state', this.state);
		var data = {
			"title": this.state.title,
			"body": this.state.body,
			"author_id": this.state.author_id,
		}
		if (data.title !== '' && data.body != '' && data.author_id != '') {
			fetch("http://127.0.0.1:1337/articles", {
				method: "POST",
				headers: {
					'Accept': 'application/json',
					'Content-Type': 'application/json',
				},
				body:  JSON.stringify(data)
			})
			.then(function(response){ 
				return response;   
			})
			.then(function(data){ 
				console.log(data);
				console.log("article successfully added");
				swal('Success!', 'Article successfully added', 'success');

			});
		} else {
			console.log("please fill all the fields");
			swal('Oops!', 'Please fill author_id, title, and body fields!', 'error');
		}

	}

	render() {
		return (
			<div className='form-inline'>
				<h2> Add an article</h2>
				<div className='form-group'>
					<input
					className='form-control'
					type='text'
					placeholder='title'
					onChange={event => this.setState({title:event.target.value})}
					/><br />
					<input
					className='form-control'
					type='textarea'
					placeholder='type your article here'
					onChange={event => this.setState({body:event.target.value})}
					/><br />
					<input
					className='form-control'
					type='text'
					placeholder='Your author id'
					onChange={event => this.setState({author_id:event.target.value})}
					/><br />
					<button
					className='btn btn-primary'
					type='button'
					onClick={() => this.createArticle()}>
					Add article
					</button>
				</div>
			</div>	
			)
	}

}

export default Article;
