import React, {Component} from 'react';
import axios from 'axios';
import swal from 'sweetalert2';
import { Button, Col, ControlLabel,FormGroup, FormControl, Table, Modal, Well } from 'react-bootstrap';


class Admin extends Component{
	constructor (props) {
		super(props);
		this.state = {
			users: [],
		};
	}

	follow(user) {
		//console.log('this.state', this.state);
			fetch("http://127.0.0.1:8000/user/follow/"+user, {
				method: "PUT",
				headers: {
					'Accept': 'application/json',
					'Content-Type': 'application/json',
					'Authorization': localStorage.getItem('token')
				}
			})
			.then(function(res){
				return res;
			})
			.then(function(data){
				console.log(data.text);
				console.log("user successfully followed");
				swal('Success!', 'Followed!', 'success');
			});
	}

	unfollow(user) {
		//console.log('this.state', this.state);
			fetch("http://127.0.0.1:8000/user/unfollow/"+user, {
				method: "PUT",
				headers: {
					'Accept': 'application/json',
					'Content-Type': 'application/json',
					'Authorization': localStorage.getItem('token')
				}
			})
			.then(function(res){
				return res;
			})
			.then(function(data){
				console.log(data.text);
				console.log("user successfully unfollowed");
				swal('Success!', 'Unfollowed!', 'success');
			});
	}

	componentDidMount() {
		axios.get('http://127.0.0.1:8000/user/users', { 'headers': { 'Authorization': localStorage.getItem("token")}})
		.then(results => {
			console.log(results.data);
			return results;
		}).then(results => {
			let users = results.data.map((user) => {
				return(
					<div>
						<Well>
							<b>{user.username} </b><br/>
							{user.email}<br/>
							Tweets: <br/>
							Followers:
							<button
							className='btn pull-right'
							type='button'
							onClick={() => this.unfollow(user.username)}>
							Unfollow
							</button>
							<button
							className='btn btn-primary pull-right'
							type='button'
							onClick={() => this.follow(user.username)}>
							Follow
							</button>
						</Well>
					</div>
				)
			})
		this.setState({users:users});
		console.log("this.state ", this.state.users);
	})
}

	render(){
		return (
			<div className = "container">
			<h2>All Users</h2>
			{this.state.users}
			</div>
			)
	}

}

export default Admin;
